mapper.select:
select
from tbl_event e inner join tbl_user_event ue on ue.event_id=e.event_id inner join tbl_user u on u.user_id=ue.user_id
e.event_id = #{eventId,jdbcType=INTEGER}
and ue.user_id = #{userId,jdbcType=INTEGER}
and ( (#{eventStartDate,jdbcType=DATE} <= e.event_start_date and e.event_start_date <= #{eventEndDate,jdbcType=DATE}) or (#{eventStartDate,jdbcType=DATE} <= e.event_end_date and e.event_end_date <= #{eventEndDate,jdbcType=DATE}) )
order by e.event_start_date, e.event_start_time

mapper.delete:
update tbl_user 
set 
delete_flag=${@org.tmhi.model.entity.BaseEntity@DELETE_FLAG_1}, 
update_user_id=#{updateUserId,jdbcType=INTEGER}, 
update_datetime=#{updateDatetime,jdbcType=TIMESTAMP}, 
version=#{version,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=INTEGER}
and delete_flag=${@org.tmhi.model.entity.BaseEntity@DELETE_FLAG_NO}
